function speak(text) {
  recognition.stop();
  let msg = new SpeechSynthesisUtterance(text);
  msg.lang = 'bn-BD';
  msg.pitch = 1; 
  msg.volume = 1; 
  window.speechSynthesis.speak(msg);
  return;
}
