 function move(e){
  document.querySelector('.p-down-con').style.display="block";
  e.style.display="none";
  var elem = document.getElementById("myBar");
  var width = 1;
  var id = setInterval(frame, pageTimer);

  function frame() {
    if (width >= 100) {
      clearInterval(id);
      myFunc();

    } else {
      width++;
      elem.innerHTML = width + "%";
      elem.style.width = width + '%';
    }
  }
}
